(function() {
    var root = exports || this;

    // Declare a process environment so that we can set
    // some globals here and have interop with node
    process.env = process.env || {};

    module.exports = root = {
        Logger          : require('splunk-sdk/lib/log').Logger,
        Context         : require('splunk-sdk/lib/context'),
        Service         : require('splunk-sdk/lib/service'),
        Http            : require('splunk-sdk/lib/http'),
        Utils           : require('splunk-sdk/lib/utils'),
        Async           : require('splunk-sdk/lib/async'),
        Paths           : require('splunk-sdk/lib/paths').Paths,
        Class           : require('splunk-sdk/lib/jquery.class').Class,
        ModularInputs   : require('splunk-sdk/lib/modularinputs')
    };
    
    if (typeof(window) === 'undefined') {
        root.NodeHttp = require('splunk-sdk/lib/platform/node/node_http').NodeHttp;
    }
})();